contract StakeInitializable is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20Metadata;

    // Address of the stake factory
    address public STAKE_FACTORY;

    // Whether it is initialized
    bool public _isInitialized;

    // The staked token
    IERC20Metadata public _stakedToken;

    // The reward token
    IERC20Metadata public _rewardToken;

    // Reward tokens created per block
    uint256 public _rewardPerBlock;

    // The block number when reward token's mining starts
    uint256 public _startBlock;

    // The block number when reward token's mining ends
    uint256 public _bonusEndBlock;

    // The block number of the last pool update
    uint256 public _lastRewardBlock;

    // Staking time periods in days
    uint256[] public _timePeriods;

    // Penalty amounts for early unstake
    uint256[] public _penalties;

    // Sum of allocation points of every time-based staking pool
    uint256 public _totalAllocPoints;

    // Whether it is time-based staking
    bool public _isTimeBasedStaking = true;

    // Whether early staking charge penalty
    bool public _isPenaltyCharged = true;

    // The precision factor
    uint256 public PRECISION_FACTOR;

    // Penalty fee denominator
    uint256 public FEE_DENOMINATOR = 100;

    // Info of each time-based staking pool
    struct PoolInfo {
        uint256 allocPoint; // How many allocation points are assignd to this pool
        uint256 lastRewardBlock; // The block number of this pool's last update
        uint256 accTokenPerShare; // Accrued reward token per share
        uint256 penalty; // Penalty amount for early unstake
        uint256 totalDeposits; // Total amount of staked tokens deposited
    }
    // Info of each pool mapped with their time periods
    mapping(uint256 => PoolInfo) public poolInfo;

    // Info of each user
    struct UserInfo {
        uint256 amount; // How many staked tokens the user has provided
        uint256 rewardDebt; // Reward debt
        uint256 claimTime; // Before this time, any claim will result in penalty
        // mapping (uint256 => PoolInfo) userPoolInfo
    }
    // Info of each user that stakes token (stakedToken) in a given pool
    mapping(address => mapping(uint256 => UserInfo)) public userInfo;

    event Deposit(
        address indexed user,
        uint256 indexed period,
        uint256 indexed amount
    );

    event Claim(
        address indexed user,
        uint256 indexed period,
        uint256 indexed amount
    );

    event Withdraw(
        address indexed user,
        uint256 indexed period,
        uint256 indexed withdrawalAmount,
        uint256 rewardAmount
    );

    event UpdateFeeDenominator(
        uint256 oldFEE_DENOMINATOR,
        uint256 newFEE_DENOMINATOR
    );

    /*
     * @notice Constructor
     */
    constructor() {
        STAKE_FACTORY = msg.sender;
    }

    /**
     * @notice Initialize the contract
     * @param stakedToken_: staked token address
     * @param rewardToken_: reward token address
     * @param rewardPerBlock_: reward per block (in reward token)
     * @param startBlock_: start block
     * @param bonusEndBlock_: end block
     * @param timePeriods_: staking intervals (in days)
     * @param penalties_: early withdrawal penalty for every staking interval
     * @param admin_:  admin address with ownership
     */
    function initialize(
        IERC20Metadata stakedToken_,
        IERC20Metadata rewardToken_,
        uint256 rewardPerBlock_,
        uint256 startBlock_,
        uint256 bonusEndBlock_,
        uint256[] memory timePeriods_,
        uint256[] memory penalties_,
        address admin_
    ) external {
        require(!_isInitialized, "Already initialized");
        require(msg.sender == STAKE_FACTORY, "Not factory");
        require(
            stakedToken_ != IERC20Metadata(address(0)) &&
                rewardToken_ != IERC20Metadata(address(0)),
            "Staked token or Reward token cannot be zero address"
        );
        require(
            rewardPerBlock_ != 0,
            "Reward Per Block must be greater than zero"
        );
        require(
            startBlock_ >= block.number,
            "Start block must be ahead or equal to the initializing block"
        );
        require(
            bonusEndBlock_ > startBlock_,
            "Staking end time must be greater than start time"
        );
        require(
            timePeriods_.length == penalties_.length,
            "Time period and penalty lengths must be equal"
        );

        if (timePeriods_.length == 1) {
            require(
                timePeriods_[0] != 0,
                "Time period cannot be zero. Enter empty array to avoid time-based staking"
            );
        }

        // Make this contract initialized
        _isInitialized = true;

        _stakedToken = stakedToken_;
        _rewardToken = rewardToken_;
        _rewardPerBlock = rewardPerBlock_;
        _startBlock = startBlock_;
        _bonusEndBlock = bonusEndBlock_;
        _timePeriods = timePeriods_;
        _penalties = penalties_;

        uint256 decimalsRewardToken = uint256(_rewardToken.decimals());
        require(decimalsRewardToken < 30, "Must be less than 30");

        // If there is no time based staking
        if (timePeriods_.length == 0) {
            _isTimeBasedStaking = false;
        }

        // If there are no penalties on early withdrawal
        if (penalties_.length == 0) {
            _isPenaltyCharged = false;
        } else {
            // Check if all the 'penalties_' elements are zero
            for (uint256 i = 0; i < penalties_.length; i++) {
                if (penalties_[i] == 0) {
                    if (i == (penalties_.length - 1)) {
                        _isPenaltyCharged = false;
                    }
                    continue;
                } else {
                    break;
                }
            }
        }

        // Create either time-based staking pools or a single zero-day staking pool
        if (_isTimeBasedStaking) {
            uint256 totalPools = _timePeriods.length;
            _totalAllocPoints = (totalPools * (totalPools + 1)) / 2; // n(n+1)/2 => Sum of an A.P. with a = d = 1

            for (uint256 i = 0; i < totalPools; i++) {
                poolInfo[_timePeriods[i]] = PoolInfo({
                    allocPoint: uint256(i + 1),
                    lastRewardBlock: _startBlock,
                    accTokenPerShare: 0,
                    penalty: _penalties[i],
                    totalDeposits: 0
                });
            }
        } else {
            _totalAllocPoints = 1;
            poolInfo[0] = PoolInfo({
                allocPoint: 1,
                lastRewardBlock: _startBlock,
                accTokenPerShare: 0,
                penalty: 0,
                totalDeposits: 0
            });
        }

        PRECISION_FACTOR = uint256(10**(uint256(30) - decimalsRewardToken));

        // Set the lastRewardBlock as the startBlock
        _lastRewardBlock = _startBlock;

        // Transfer ownership to the admin address who becomes owner of the contract
        transferOwnership(admin_);
    }

    /**
     * @notice Deposit staked tokens
     * @param amount_: amount to deposit (in staked tokens)
     * @param period_: staking interval
     */
    function deposit(uint256 amount_, uint256 period_) public nonReentrant {
        require(amount_ > 0, "Deposit amount must be greater than zero");
        // Check if the entered 'period_' exists / valid
        if (_isTimeBasedStaking) {
            require(
                _searchArray(_timePeriods, period_),
                "Invalid staking period"
            );
        } else {
            require(period_ == 0, "Invalid staking period, enter zero");
        }

        PoolInfo storage pool = poolInfo[period_];
        UserInfo storage user = userInfo[msg.sender][period_];

        _updatePool(period_);

        // Transfer staked token to this contract
        _stakedToken.safeTransferFrom(
            address(msg.sender),
            address(this),
            amount_
        );

        // Update the storage variables
        pool.totalDeposits += amount_;
        user.amount += amount_;
        user.rewardDebt =
            (user.amount * pool.accTokenPerShare) /
            PRECISION_FACTOR;
        if (_isPenaltyCharged) {
            user.claimTime = block.timestamp + _inSeconds(period_);
        }

        emit Deposit(msg.sender, period_, amount_);
    }

    /**
     * @notice Claim reward tokens (if any)
     * @param period_: staking interval
     */
    function claim(uint256 period_) public nonReentrant {
        // Check if the entered 'period_' exists / valid
        if (_isTimeBasedStaking) {
            require(
                _searchArray(_timePeriods, period_),
                "Invalid staking period"
            );
        } else {
            require(period_ == 0, "Invalid staking period, enter zero");
        }

        PoolInfo storage pool = poolInfo[period_];
        UserInfo storage user = userInfo[msg.sender][period_];

        // Users can claim their rewards if:
        // 1) there is no penalty being charged
        // 2) user has exceeded the claim time
        // 3) there is no penalty in this particular staking period
        require(
            (!_isPenaltyCharged) ||
                (user.claimTime <= block.timestamp) ||
                (pool.penalty == 0),
            "Claim time is not yet reached"
        );
        require(user.amount > 0, "User has not yet staked any tokens");

        _updatePool(period_);

        uint256 pendingRewards = ((user.amount * pool.accTokenPerShare) /
            PRECISION_FACTOR) - user.rewardDebt;
        if (pendingRewards > 0) {
            _rewardToken.safeTransfer(address(msg.sender), pendingRewards);
        }
        user.rewardDebt =
            (user.amount * pool.accTokenPerShare) /
            PRECISION_FACTOR;

        emit Claim(msg.sender, period_, pendingRewards);
    }

    /**
     * @notice Withdraw staked tokens
     * @param amount_: amount to withdraw (in staked tokens)
     * @param period_: staking interval
     */
    function withdraw(uint256 amount_, uint256 period_) public nonReentrant {
        require(amount_ > 0, "Withdraw amount must be greater than zero");
        // Check if the entered 'period_' exists / valid
        if (_isTimeBasedStaking) {
            require(
                _searchArray(_timePeriods, period_),
                "Invalid staking period"
            );
        } else {
            require(period_ == 0, "Invalid staking period, enter zero");
        }

        PoolInfo storage pool = poolInfo[period_];
        UserInfo storage user = userInfo[msg.sender][period_];

        require(
            user.amount >= amount_,
            "Withdrawal amount exceeds staked amount"
        );

        _updatePool(period_);

        uint256 pendingRewards = ((user.amount * pool.accTokenPerShare) /
            PRECISION_FACTOR) - user.rewardDebt;

        // Distribute rewards, if any
        if (pendingRewards > 0) {
            if (
                (!_isPenaltyCharged) ||
                (user.claimTime <= block.timestamp) ||
                (pool.penalty == 0)
            ) {
                _rewardToken.safeTransfer(address(msg.sender), pendingRewards);
            } else {
                // Time has not yet reached, so charge a penalty on rewards
                pendingRewards -=
                    (pendingRewards * pool.penalty) /
                    FEE_DENOMINATOR;
                _rewardToken.safeTransfer(address(msg.sender), pendingRewards);
            }
        }

        pool.totalDeposits -= amount_;
        user.amount -= amount_;
        _stakedToken.safeTransfer(address(msg.sender), amount_);
        user.rewardDebt =
            (user.amount * pool.accTokenPerShare) /
            PRECISION_FACTOR;

        emit Withdraw(msg.sender, period_, amount_, pendingRewards);
    }

    /*
     * @notice Update reward variables of the given pool to be up-to-date
     * @param pid_: staking pool to update
     */
    function _updatePool(uint256 pid_) internal {
        PoolInfo storage pool = poolInfo[pid_];

        if (block.number <= pool.lastRewardBlock) {
            return;
        }

        // uint256 stakedTokenSupply = _stakedToken.balanceOf(address(this));
        uint256 stakedTokenSupply = pool.totalDeposits;

        if (stakedTokenSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }

        uint256 multiplier = _getMultiplier(pool.lastRewardBlock, block.number);
        uint256 tokenReward = (multiplier * _rewardPerBlock * pool.allocPoint) /
            _totalAllocPoints;
        pool.accTokenPerShare =
            pool.accTokenPerShare +
            (tokenReward * PRECISION_FACTOR) /
            stakedTokenSupply;
        pool.lastRewardBlock = block.number;
        _lastRewardBlock = block.number;
    }

    /**
     * @notice Return reward multiplier over the given 'from_' to 'to_' block
     * @param from_: block to start
     * @param to_: block to finish
     */
    function _getMultiplier(uint256 from_, uint256 to_)
        internal
        view
        returns (uint256 multiplier)
    {
        if (to_ <= _bonusEndBlock) {
            multiplier = to_ - from_;
        } else if (from_ >= _bonusEndBlock) {
            multiplier = 0;
        } else {
            multiplier = _bonusEndBlock - from_;
        }
    }

    /**
     * @dev Calculates the number of seconds
     * @param days_: number of days
     */
    function _inSeconds(uint256 days_) private pure returns (uint256) {
        uint256 secondsInDays = days_ * 24 * 60 * 60;
        return secondsInDays;
    }

    /**
     * @dev Checks if a given array contains the passed value
     * @param array_: an array of unsigned integers
     * @param value_: value to search in the given array
     */
    function _searchArray(uint256[] memory array_, uint256 value_)
        private
        pure
        returns (bool)
    {
        bool exists = false;
        for (uint256 i = 0; i < array_.length; i++) {
            if (array_[i] == value_) {
                exists = true;
                break;
            }
        }
        return exists;
    }

    /**
     * @notice Set Fee Denominator
     * @param newDenominator_: new fee denominator
     */
    function setFEE_DENOMINATOR(uint256 newDenominator_) public onlyOwner {
        uint256 oldFEE_DENOMINATOR = FEE_DENOMINATOR;
        FEE_DENOMINATOR = newDenominator_;

        emit UpdateFeeDenominator(oldFEE_DENOMINATOR, FEE_DENOMINATOR);
    }
}

contract StakeTokenFactory is Ownable {
    event NewStakingContract(address indexed stakingContract);

    constructor() {}

    /*
     * @notice Deploy the staking pool
     * @param stakedToken_: staked token address
     * @param rewardToken_: reward token address
     * @param rewardPerBlock_: reward per block (in reward token)
     * @param startBlock_: start block
     * @param bonusEndBlock_: end block
     * @param timePeriods_: staking intervals (in days)
     * @param penalties_: early withdrawal penalty for every staking interval
     * @param admin_:  admin address with ownership
     */
    function initializeStakingContract(
        IERC20Metadata stakedToken_,
        IERC20Metadata rewardToken_,
        uint256 rewardPerBlock_,
        uint256 startBlock_,
        uint256 bonusEndBlock_,
        uint256[] calldata timePeriods_, // Input in ascending order
        uint256[] calldata penalties_,
        address admin_
    ) external onlyOwner {
        bytes memory bytecode = type(StakeInitializable).creationCode;
        // pass constructor argument
        bytecode = abi.encodePacked(bytecode);
        bytes32 salt = keccak256(abi.encodePacked(stakedToken_, rewardToken_));
        address stakeInitializableAddress;

        assembly {
            stakeInitializableAddress := create2(
                0,
                add(bytecode, 32),
                mload(bytecode),
                salt
            )
        }

        StakeInitializable(stakeInitializableAddress).initialize(
            stakedToken_,
            rewardToken_,
            rewardPerBlock_,
            startBlock_,
            bonusEndBlock_,
            timePeriods_,
            penalties_,
            admin_
        );

        emit NewStakingContract(stakeInitializableAddress);
    }
}
